from os import getenv, makedirs, pathimport loggingfrom pathlib import PathTOKEN = getenv('TOKEN')assert TOKENDB_HOST = getenv('DB_HOST', 'localhost')DB_PORT = getenv('DB_PORT', '5432')DB_USER = getenv('DB_USER', 'postgres')DB_PASSWORD = getenv("DB_PASSWORD", 'password')DB_NAME = getenv('DB', 'postgres')LOG_LEVEL = getenv('LOG_LEVEL', 'INFO')SERVER_FLAG = getenv('SERVER_FLAG', None)DOWNLOAD_PATH = Path(getenv('DOWNLOAD_PATH', '/opt/bass/download/'))BASS_PATH = Path(getenv('BASS_PATH', "/opt/bass/boosted/"))LOG_PATH = Path(getenv('LOG_FOLDER', '/var/log/bass_bot/'))WH_HOST = getenv('WH_HOST', '')WH_PORT = int(getenv("WH_PORT", "8443"))WH_LISTEN = getenv('WH_LISTEN', '0.0.0.0')WH_SSL_CERT = getenv("SSL_CERT", 'WH_cert.pem')WH_SSL_PRIV = getenv('SSL_PRIV', 'WH_pkey.pem')  # TODO REMOVEWH_URL_BASE = "https://%s:%s" % (WH_HOST, WH_PORT)WH_URL_PATH = "/%s/" % TOKENformatter = logging.Formatter('%(asctime)s %(levelname)s:%(message)s')file_log = logging.getLogger('bass_boost')sys_log = logging.getLogger('bass_boost_sys')info_file_handler = logging.FileHandler(f'{str(LOG_PATH / "info.log")}')  # TODO stop writing to file maybeinfo_file_handler.setFormatter(formatter)info_file_handler.setLevel(logging.INFO)system_log_file_handler = logging.FileHandler(f'{str(LOG_PATH / "system.log")}')system_log_file_handler.setFormatter(formatter)system_log_file_handler.setLevel(LOG_LEVEL)file_log.addHandler(info_file_handler)sys_log.addHandler(system_log_file_handler)file_log.setLevel(logging.INFO)sys_log.setLevel(LOG_LEVEL)sys_log.info('GOT THIS VARS'             f'SSL_PRIV={WH_SSL_PRIV}'             f'SSL_CERT={WH_SSL_CERT}')  # todo